// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/// @author philogy <https://github.com/philogy>

struct tuint256 {
    uint256 __placeholder;
}

struct taddress {
    uint256 __placeholder;
}

using TransientPrimitivesLib for tuint256 global;

using TransientPrimitivesLib for taddress global;

library TransientPrimitivesLib {
    error ArithmeticOverflowUnderflow();

    function get(tuint256 storage ptr) internal view returns (uint256 value) {
        /// @solidity memory-safe-assembly
        assembly {
            value := tload(ptr.slot)
        }
    }

    function get(taddress storage ptr) internal view returns (address value) {
        /// @solidity memory-safe-assembly
        assembly {
            value := tload(ptr.slot)
        }
    }

    function set(tuint256 storage ptr, uint256 value) internal {
        /// @solidity memory-safe-assembly
        assembly {
            tstore(ptr.slot, value)
        }
    }

    function inc(
        tuint256 storage ptr,
        uint256 change
    ) internal returns (uint256 newValue) {
        ptr.set(newValue = ptr.get() + change);
    }

    function dec(
        tuint256 storage ptr,
        uint256 change
    ) internal returns (uint256 newValue) {
        ptr.set(newValue = ptr.get() - change);
    }

    function inc(
        tuint256 storage ptr,
        int256 change
    ) internal returns (uint256 newValue) {
        uint256 currentValue = ptr.get();
        assembly ("memory-safe") {
            newValue := add(currentValue, change)
            if iszero(eq(lt(newValue, currentValue), slt(change, 0))) {
                mstore(0x00, 0xc9654ed4 /* ArithmeticOverflowUnderflow() */)
                revert(0x1c, 0x04)
            }
        }
        ptr.set(newValue);
    }

    function dec(
        tuint256 storage ptr,
        int256 change
    ) internal returns (uint256 newValue) {
        uint256 currentValue = ptr.get();
        assembly ("memory-safe") {
            newValue := sub(currentValue, change)
            if iszero(eq(lt(newValue, currentValue), sgt(change, 0))) {
                mstore(0x00, 0xc9654ed4 /* ArithmeticOverflowUnderflow() */)
                revert(0x1c, 0x04)
            }
        }
        ptr.set(newValue);
    }

    function set(taddress storage ptr, address value) internal {
        /// @solidity memory-safe-assembly
        assembly {
            tstore(ptr.slot, value)
        }
    }

    error IndexOutOfBounds();
}

contracts/Structures.sol.content:
// SPDX-License-Identifier: BSL-1.1
pragma solidity ^0.8.24;

// Enums
/**
 * @dev Enum representing the possible actions for vault operations.
 * Used in startAction and performAction to determine the operation type.
 */
enum Choice {
    DEPOSIT,
    MINT,
    WITHDRAW_PERIOD
}

// Structs
/**
 * @dev Struct to store information about a vault action initiated by a user.
 * This information is saved during startAction and used in performAction.
 */
struct VaultAction {
    address msgSender; // Address of the user initiating the action
    address receiver; // Address to receive tokens or shares
    uint256 amount; // Amount of tokens or shares for the action
    Choice choice; // Type of action to perform
    uint256 slippage; // Allowed slippage for the action
}

/**
 * @dev Structure to hold initial information for vault setup.
 */
struct StartInfo {
    address factoryOwner;
    address vaultManager;
    address chainLinkToken;
    address oracleAddress;
    address gainsAddress;
}

/**
 * @dev Struct to store reward information for strategy execution.
 */
struct RewardInfo {
    uint256 masterFee;
    uint256 feeMultiplier;
    address caller;
}

